{
	int x;
	int y;
	int step;
};
queue<point> r;
int main()
{
	cin>>n>>m;
	cin>>startx>>starty;
	for(int i=1;i<=n;i++){
		for(int j=1;j<=m;j++){
			cin>>a[i][j];
		} 
	}
	point start;
	start.x=startx;
	start.y=starty;
	start.step=0;
	r.push(start);
	v[startx][starty]=1;
	while(!r.empty()){
		int x=r.front().x;
		int y=r.front().y;
		int step=r.front().step;
		if(x==n&&y==m){
			flag=1;
			cout<<r.front().step;
			break;
		}
		for(int i=0;i<4;i++){
			int tx=x+dx[i];
			int ty=y+dy[i];
			if(v[tx][ty]==0&&a[tx][ty]==1){
